<script>
export default {
  data() {
    return {
      selectedArea: 'ticket_bgs',
      tk_bg_color: '#FFFFFF', 
      tk_stk_color: '#000000',
      tk_right_color: '#CCCCCC',
      subTitColor: '#000000',       
      subsubTitColor: '#000000',    
      TitColor1: '#000000',         
      TitColor2: '#000000',         
      TitColor3: '#000000',
      gradientCounter: 0  
    };
  },
  methods: {
    changeColors(...colors) {
      if (colors.length === 3) {
        // 如果传入了3个颜色，处理为 ticket_bgs 的颜色
        const [bgColor, stkColor, rightColor] = colors;
        this.applyColors(bgColor, stkColor, rightColor);
      } else if (colors.length === 5) {
        // 如果传入了5个颜色，处理为 ticket_colors 的颜色
        const [subTitColor, subsubTitColor, TitColor1, TitColor2, TitColor3] = colors;
        this.subTitColor = subTitColor;
        this.subsubTitColor = subsubTitColor;
        this.TitColor1 = TitColor1;
        this.TitColor2 = TitColor2;
        this.TitColor3 = TitColor3;
      }
    },
    applyColors(bgColor, stkColor, rightColor) {
      if (bgColor.startsWith('linear-gradient')) {
        this.gradientCounter += 1; 
        const gradientId = `customGradient${this.gradientCounter}`;
        this.createGradient(bgColor, gradientId);
        this.tk_bg_color = `url(#${gradientId})`;
      } else {
        this.tk_bg_color = bgColor;
      }
      this.tk_stk_color = stkColor;
      this.tk_right_color = rightColor;
    },
    createGradient(gradientString, gradientId) {
      const gradient = document.createElementNS('http://www.w3.org/2000/svg', 'linearGradient');
      gradient.setAttribute('id', gradientId);
      gradient.setAttribute('x1', '0%');
      gradient.setAttribute('y1', '0%');
      gradient.setAttribute('x2', '100%');
      gradient.setAttribute('y2', '0%');

      const colors = gradientString.match(/#[0-9A-Fa-f]{6}/g);
      const stops = gradientString.match(/(\d+(\.\d+)?%)/g);

      colors.forEach((color, index) => {
        const stop = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
        stop.setAttribute('offset', stops[index]);
        stop.setAttribute('style', `stop-color:${color};stop-opacity:1`);
        gradient.appendChild(stop);
      });

      const defs = document.querySelector('svg defs');
      if (defs) {
        defs.appendChild(gradient);
      } else {
        const svg = document.querySelector('svg');
        const defs = document.createElementNS('http://www.w3.org/2000/svg', 'defs');
        defs.appendChild(gradient);
        svg.insertBefore(defs, svg.firstChild);
      }
    }
  }
};
</script>





  
<script setup>
    import { ref, onMounted } from 'vue'

    const pathData1 = ref('')
